import React from 'react';
import './App.css';
import { BrowserRouter as Router, Link, NavLink} from 'react-router-dom';
import Route from 'react-router-dom/Route'; 

const User = ({match}) =>{
    return (<h1>User {match.params.username} </h1>);
}

function App() {
  state = {
    loggedIn : false
  }

  return (
  <Router>

    <div className="App">

    <ul>
    <li><NavLink to="/" activeStyle={
      {color:'green'}
    }>Home</NavLink></li>
    <li><NavLink to="/about" aactiveStyle={
      {color:'green'}
    }>About</NavLink></li>
    </ul>

    <input type="button" value="Entrar"/>

    <Route path="/" exact={true} render ={ () => {return(<h1>Welcome</h1>);}}></Route>
    <Route path="/about" render ={ () => {return(<h1>About</h1>);}}></Route>
    <Route path="/user/:username" component={User}></Route>
    </div>

    </Router>

  );
}

export default App;
